#include <stdio.h>

int main() {
    int n, sum;

    // Input the value of n
    printf("Enter a positive integer: ");
    scanf("%d", &n);

    sum = n * (n + 1) / 2;

    printf("Sum of first %d natural numbers is: %d\n", n, sum);

